import { Formik, Form, Field, ErrorMessage } from "formik"
import * as Yup from "yup"
import axios from "axios"
import { useNavigate, } from "react-router-dom"
import { useState , useEffect } from "react";
import { useParams } from 'react-router-dom';


// const apiUrl = process.env.REACT_APP_API_URL;

interface Recipe {
    title: string;
    author: string;
    username: string;
    timeToCook: number;
    ingredients: string[];
    instructions: string[];
}

export function SingleRecipe() {

    const { recipeId } = useParams();
    const [recipe, setRecipe] = useState<Recipe | null>(null);


    useEffect(() => {
        axios.get<Recipe>(`http://localhost:3001/recipes/${recipeId}`)
          .then((response) => {
            setRecipe(response.data);
          })
          .catch((error) => {
            console.error('Error fetching recipe:', error);
          });
    }, [recipeId]);

    return (
        <div className="single-recipe-page h-full w-screen flex flex-col items-center justify-center font-oswald">
            <div className='single-recipe-header w-full bg-slate-700 flex justify-start text-center'>
                <div className='single-recipe-text-container mt-36 mb-4 ml-32'>

                <h1 className='text-6xl text-white'>
                    {recipe ? recipe.title : "Loading..."}
                </h1>

                </div>
            </div>
            <div className="recipe-detail-container py-16 mr-48">
                {recipe ? (
                    <div>
                    <h2 className='text-7xl mb-4'>{recipe.title}</h2>
                    <h2 className='text-5xl mb-4'>Posted By: {recipe.username}</h2>
                    <h2 className='text-5xl mb-4'>Time to Cook: {recipe.timeToCook} minutes</h2>

                    <div>
                        <h3 className='text-5xl mb-4'>Ingredients:</h3>
                        <ul>
                        {recipe.ingredients.map((ingredient, index) => (
                            <li key={index}>{ingredient}</li>
                        ))}
                        </ul>
                    </div>

                    <div>
                        <h3 className='text-5xl mb-4'>Instructions:</h3>
                        <ol>
                        {recipe.instructions.map((instruction, index) => (
                            <li key={index}>{instruction}</li>
                        ))}
                        </ol>
                    </div>
                    </div>
                ) : (
                    <p>Loading...</p>
                )}
            </div>

        </div>
    )

}
