import { Formik, Form, Field, ErrorMessage } from "formik"
import * as Yup from "yup"
import axios from "axios"
import { useNavigate } from "react-router-dom"
import { useState } from "react";



// const apiUrl = process.env.REACT_APP_API_URL;


export function Create() {

    let navigate = useNavigate()

    interface Recipe {
        title: string;
        timeToCook: number;
        ingredients: string[];
        instructions: string[];
    }

    // const initialValues : Recipe = {
    //     title: "",
    //     body: "",
    //     date: "",
    // }

    const [recipeData, setRecipeData] = useState<Recipe>({
        title: "",
        timeToCook: 0,
        ingredients: [],
        instructions: [],
    });

    const onSubmitA = (data: Recipe) => {
        // axios.post(`${apiUrl}/announcements`, data).then((response) => {
        axios.post("http://localhost:3001/recipes", data).then((response) => {

          navigate("/recipes");
        });
      };


    const validationSchema = Yup.object().shape({
    title: Yup.string().required("Title is required"),
    timeToCook: Yup.number().required("Time to cook is required").positive().integer(),
    ingredients: Yup.array().of(
        Yup.object().shape({
        name: Yup.string().required("Ingredient name is required"),
        quantity: Yup.string().required("Quantity is required"),
        })
    ),
    instructions: Yup.array().of(Yup.string().required("Instruction is required")),
    });

    return (
        <div className="create-recipe-page h-full w-screen flex flex-col items-center justify-center font-oswald">
            {/* ... (Header and container divs) ... */}
            <div className="create-container w-3/4 md:w-1/3 my-16 p-8 text-center border-4 border-slate-800">
            <form onSubmit={handleSubmit} className="formContainer flex flex-col">
                <label className="text-3xl mb-2"> Title: </label>
                <input
                type="text"
                name="title"
                value={recipeData.title}
                onChange={handleChange}
                placeholder="ex...Title"
                className="border-2 p-2 mb-4"
                />

                <label className="text-3xl mb-2"> Time to Cook (minutes): </label>
                <input
                type="number"
                name="timeToCook"
                value={recipeData.timeToCook}
                onChange={handleChange}
                placeholder="ex...30"
                className="border-2 p-2 mb-4"
                />

                <div className="mb-4">
                <label className="text-3xl"> Ingredients: </label>
                <div className="flex items-center">
                    <input
                    type="text"
                    name="ingredients"
                    value={recipeData.ingredients}
                    onChange={handleChange}
                    placeholder="Add an ingredient..."
                    className="border-2 p-2"
                    />
                    <button
                    type="button"
                    onClick={() => handleAddItem("ingredients")}
                    className="ml-2 bg-slate-800 text-white px-2 py-1 rounded-full"
                    >
                    +
                    </button>
                </div>
                <ul>
                    {recipeData.ingredients.map((ingredient, index) => (
                    <li key={index}>{ingredient}</li>
                    ))}
                </ul>
                </div>

                <div className="mb-4">
                <label className="text-3xl"> Instructions: </label>
                <div className="flex items-center">
                    <input
                    type="text"
                    name="instructions"
                    value={recipeData.instructions}
                    onChange={handleChange}
                    placeholder="Add an instruction..."
                    className="border-2 p-2"
                    />
                    <button
                    type="button"
                    onClick={() => handleAddItem("instructions")}
                    className="ml-2 bg-slate-800 text-white px-2 py-1 rounded-full"
                    >
                    +
                    </button>
                </div>
                <ul>
                    {recipeData.instructions.map((instruction, index) => (
                    <li key={index}>{instruction}</li>
                    ))}
                </ul>
                </div>

                <button
                type="submit"
                className="mt-4 px-4 py-2 text-white bg-slate-800 font-semibold hover:bg-opacity-75 transition duration-200"
                >
                Create Recipe
                </button>
            </form>
            </div>
        </div>
        );

}
