import { Formik, Form, Field, ErrorMessage } from "formik"
import * as Yup from "yup"
import axios from "axios"
import { useNavigate } from "react-router-dom"


// const apiUrl = process.env.REACT_APP_API_URL;


export function Create() {

    interface Announcement {
        title: string;
        body: string;
        date: string;
      }

    let navigate = useNavigate()

    const initialValues : Announcement = {
        title: "",
        body: "",
        date: "",
    }

    const onSubmitA = (data: Announcement) => {
        // axios.post(`${apiUrl}/announcements`, data).then((response) => {
        axios.post("http://localhost:3001/auth/login", data).then((response) => {

          navigate("/events");
        });
      };


    const validationSchema = Yup.object().shape({
    title: Yup.string().required("Title is required"),
    timeToCook: Yup.number().required("Time to cook is required").positive().integer(),
    ingredients: Yup.array().of(
        Yup.object().shape({
        name: Yup.string().required("Ingredient name is required"),
        quantity: Yup.string().required("Quantity is required"),
        })
    ),
    instructions: Yup.array().of(Yup.string().required("Instruction is required")),
    });

return (
    <div className="create-recipe-page h-full w-screen flex flex-col items-center justify-center font-oswald">
        <div className='create-header w-full bg-slate-700 flex justify-center md:justify-start text-center'>
        <div className='create-text-container mt-36 mb-4 md:ml-32'>
            <h1 className='text-6xl text-white'>
            CREATE A RECIPE
            </h1>
        </div>
        </div>
        <div className="create-container w-3/4 md:w-1/3 my-16 p-8 text-center border-4 border-slate-800">
        <Formik initialValues={initialValues} onSubmit={onSubmitA} validationSchema={validationSchema}>
            <Form className="formContainer flex flex-col">
            <label className="text-3xl mb-2"> Title: </label>
            <ErrorMessage name="title" component="span" />
            <Field id="inputCreatePost" name="title" placeholder="ex...Title" className="border-2 p-2 mb-4" />

            <label className="text-3xl mb-2"> Time to Cook (minutes): </label>
            <ErrorMessage name="timeToCook" component="span" />
            <Field id="inputCreatePost" name="timeToCook" type="number" placeholder="ex...30" className="border-2 p-2 mb-4" />

            <label className="text-3xl mb-2"> Ingredients (comma-separated): </label>
            <ErrorMessage name="ingredients" component="span" />
            <Field id="inputCreatePost" name="ingredients" placeholder="ex...Ingredient 1, Ingredient 2, Ingredient 3" className="border-2 p-2 mb-4" />

            <label className="text-3xl mb-2"> Instructions (comma-separated): </label>
            <ErrorMessage name="instructions" component="span" />
            <Field id="inputCreatePost" name="instructions" placeholder="ex...Step 1, Step 2, Step 3" className="border-2 p-2 mb-4" />

            <button type="submit" className="mt-4 px-4 py-2 text-white bg-slate-800 font-semibold hover:bg-opacity-75 transition duration-200">
                Create Recipe
            </button>
            </Form>
        </Formik>
        </div>
    </div>
    );

}
